mapper.insert:
INSERT INTO chat (
                 room_no
                 , prod_no
                 , send_id
                 , recipient_id
                 , content
                 ,sendtime
) VALUES (
          #{roomNo}
, #{prodNo}
, #{sendId}
, #{recipientId}
, #{content}
, NOW()
)

mapper.update:
UPDATE
    chat
SET
    read_count = 0
WHERE
    prod_no = #{prodNo}
  AND
    room_no = #{roomNo}
AND
    recipient_id = #{recipientId}

mapper.select:
SELECT
    COUNT(read_count)
FROM
    chat
WHERE
    prod_no = #{prodNo}
AND
    room_no = #{roomNo}
AND
    recipient_id = #{recipientId}
AND
    read_count = 1;
