mapper.insert:
INSERT INTO prod (
                   prod_nm,
                   prod_prc
                 , cat_no
                 , sel_id
                 , way
                 , nego
                 , wday
                 , prod_co
                 , content
                 , img_count
) VALUES (
          #{prodNm},
#{prodPrc}
, #{catNo}
, #{selId}
, #{way}
, #{nego}
, NOW()
, #{prodCo}
, #{content}
, #{imgCount}
)

mapper.update:
UPDATE
    prod
SET
    wday = NOW()
  , prod_nm = #{prodNm}
  , prod_prc = #{prodPrc}
  , cat_no = #{catNo}
  , way = #{way}
  , nego = #{nego}
  , prod_co = #{prodCo}
  , content = #{content}
WHERE
    prod_no = #{prodNo}
AND
    sel_id = #{selId}

mapper.select:
SELECT
    prod_no
FROM
    prod
WHERE
    sel_id = #{selId}
AND
    cat_no = #{catNo}
AND
    prod_nm = #{prodNm}
AND
    way=#{way}
